source qa/cmdLine/bin/initialize.sh $BASH_SOURCE[0] $BASH_SOURCE[0] oracle upload $YADAMU_TESTNAME
export YADAMU_PARSER="SQL"
export SCHEMA_VERSION=1
mysql -u$DB_USER -p$DB_PWD -h$DB_HOST -D$DB_DBNAME -P$DB_PORT -v -f --init-command="set @ID=$SCHEMA_VERSION; set @METHOD='$YADAMU_PARSER';" <$YADAMU_SQL_PATH/RECREATE_ORACLE_ALL.sql >>$YADAMU_LOG_PATH/RECREATE_SCHEMA.log
source $YADAMU_SCRIPT_PATH/upload_operations_Oracle.sh $YADAMU_IMPORT_ORACLE $SCHEMA_VERSION ""
source $YADAMU_SCRIPT_PATH/export_operations_Oracle.sh $YADAMU_OUTPUT_PATH $SCHEMA_VERSION $SCHEMA_VERSION $MODE
export PRIOR_VERSION=$SCHEMA_VERSION
let "SCHEMA_VERSION+=1"
mysql -u$DB_USER -p$DB_PWD -h$DB_HOST -D$DB_DBNAME -P$DB_PORT -v -f --init-command="set @ID=$SCHEMA_VERSION; set @METHOD='$YADAMU_PARSER';" <$YADAMU_SQL_PATH/RECREATE_ORACLE_ALL.sql >>$YADAMU_LOG_PATH/RECREATE_SCHEMA.log
source $YADAMU_SCRIPT_PATH/upload_operations_Oracle.sh $YADAMU_OUTPUT_PATH $SCHEMA_VERSION $PRIOR_VERSION 
mysql -u$DB_USER -p$DB_PWD -h$DB_HOST -D$DB_DBNAME -P$DB_PORT -f --init-command="set @ID1=$PRIOR_VERSION; set @ID2=$SCHEMA_VERSION; set @METHOD='$YADAMU_PARSER'" --table <$YADAMU_SQL_PATH/COMPARE_ORACLE_ALL.sql >>$YADAMU_LOG_PATH/COMPARE_SCHEMA.log
source $YADAMU_SCRIPT_PATH/export_operations_Oracle.sh $YADAMU_OUTPUT_PATH $SCHEMA_VERSION $SCHEMA_VERSION $MODE 
node $YADAMU_QA_JSPATH/compareFileSizes $YADAMU_LOG_PATH $YADAMU_EXPORT_PATH $YADAMU_OUTPUT_PATH
node $YADAMU_QA_JSPATH/compareArrayContent $YADAMU_LOG_PATH $YADAMU_EXPORT_PATH $YADAMU_OUTPUT_PATH false