source qa/cmdLine/bin/initialize.sh $1 $BASH_SOURCE[0] mysql upload $YADAMU_TESTNAME
export YADAMU_PARSER="SQL"
export SCHEMA=sakila
export SCHEMA_VERSION=1
export FILENAME=sakila
sqlplus $DB_USER/$DB_PWD@$DB_CONNECTION @$YADAMU_SQL_PATH/RECREATE_SCHEMA.sql $YADAMU_LOG_PATH $SCHEMA$SCHEMA_VERSION 
source $YADAMU_BIN/upload.sh --RDBMS=$YADAMU_VENDOR USERID=$DB_USER/$DB_PWD@$DB_CONNECTION  FILE=$YADAMU_IMPORT_MYSQL/$FILENAME.json ENCRYPTION=false TO_USER=\"$SCHEMA$SCHEMA_VERSION\" MODE=$MODE LOG_FILE=$YADAMU_IMPORT_LOG  EXCEPTION_FOLDER=$YADAMU_LOG_PATH
source $YADAMU_BIN/export.sh --RDBMS=$YADAMU_VENDOR USERID=$DB_USER/$DB_PWD@$DB_CONNECTION  FILE=$YADAMU_OUTPUT_PATH/$FILENAME$SCHEMA_VERSION.json ENCRYPTION=false FROM_USER=\"$SCHEMA$SCHEMA_VERSION\" MODE=$MODE LOG_FILE=$YADAMU_EXPORT_LOG  EXCEPTION_FOLDER=$YADAMU_LOG_PATH
export PRIOR_VERSION=$SCHEMA_VERSION
let "SCHEMA_VERSION+=1"
sqlplus $DB_USER/$DB_PWD@$DB_CONNECTION @$YADAMU_SQL_PATH/RECREATE_SCHEMA.sql $YADAMU_LOG_PATH $SCHEMA$SCHEMA_VERSION 
