sqlplus $DB_USER/$DB_PWD@$DB_CONNECTION @$YADAMU_SQL_PATH/COMPARE_SCHEMA.sql $YADAMU_LOG_PATH $SCHEMA $PRIOR_VERSION $SCHEMA_VERSION $YADAMU_PARSER $MODE
